CREATE TRIGGER delete_movie AFTER DELETE ON movie BEGIN DELETE FROM actorlinkmovie WHERE idMovie=old.idMovie; DELETE FROM art WHERE media_id=old.idMovie AND media_type='movie'; DELETE FROM countrylinkmovie WHERE idMovie=old.idMovie; DELETE FROM directorlinkmovie WHERE idMovie=old.idMovie; DELETE FROM genrelinkmovie WHERE idMovie=old.idMovie; DELETE FROM movielinktvshow WHERE idMovie=old.idMovie; DELETE FROM studiolinkmovie WHERE idMovie=old.idMovie; DELETE FROM taglinks WHERE idMedia=old.idMovie AND media_type='movie'; DELETE FROM writerlinkmovie WHERE idMovie=old.idMovie; DELETE FROM MoviesAStreams WHERE MovieID=old.idMovie; DELETE FROM MoviesSets WHERE MovieID=old.idMovie; DELETE FROM MoviesSubs WHERE MovieID=old.idMovie; DELETE FROM MoviesVStreams WHERE MovieID=old.idMovie; END;
