CREATE TABLE tvshow ( idShow integer PRIMARY KEY AUTOINCREMENT, idSource integer NOT NULL, ListTitle text, "New" bool DEFAULT 0, Mark bool NOT NULL DEFAULT 0, TVShowPath text NOT NULL, TVDB text, Lock bool NOT NULL DEFAULT 0, EpisodeGuide text, Plot text, Premiered text, MPAA text, Rating text, NfoPath text, Language text, Ordering integer NOT NULL DEFAULT 0, Status text, ThemePath text, EFanartsPath text, Runtime text, Title text, Votes text, EpisodeSorting integer NOT NULL DEFAULT 0, SortTitle text, strIMDB text, strTMDB text, strOriginalTitle text );
CREATE UNIQUE INDEX UniqueTVShowPath ON tvshow (TVShowPath);
CREATE TRIGGER delete_tvshow AFTER DELETE ON tvshow BEGIN DELETE FROM actorlinktvshow WHERE idShow=old.idShow; DELETE FROM art WHERE media_id=old.idShow AND media_type='tvshow'; DELETE FROM creatorlinktvshow WHERE idShow=old.idShow; DELETE FROM countrylinktvshow WHERE idShow=old.idShow; DELETE FROM directorlinktvshow WHERE idShow=old.idShow; DELETE FROM genrelinktvshow WHERE idShow=old.idShow; DELETE FROM movielinktvshow WHERE idShow=old.idShow; DELETE FROM seasons WHERE idShow=old.idShow; DELETE FROM studiolinktvshow WHERE idShow=old.idShow; DELETE FROM taglinks WHERE idMedia=old.idShow AND media_type='tvshow'; END;
