CREATE TRIGGER delete_season AFTER DELETE ON season BEGIN DELETE FROM art WHERE idMedia=old.idSeason AND media_type='season'; DELETE FROM episode WHERE season=old.season AND idShow=old.idShow; DELETE FROM uniqueid WHERE idMedia=old.idSeason AND media_type='season'; END;
INSERT INTO season (idSeason, idShow, season, title, locked, marked, "new", aired, plot) SELECT idSeason, idShow, Season, SeasonText, Lock, Mark, "New", strAired, strPlot FROM seasons;
DROP TABLE seasons;

